
var mySwiper=new Swiper('.swiper-container',{slidesPerView:1,loop:true,pagination:{el:'.swiper-pagination',type:'fraction',},navigation:{nextEl:'.swiper-button-next',prevEl:'.swiper-button-prev',},scrollbar:{el:'.swiper-scrollbar',},});var navMain=document.querySelector('.main-nav');var navToggle=document.querySelector('.main-nav__toggle');navMain.classList.remove('main-nav--nojs');navToggle.addEventListener('click',function(){if(navMain.classList.contains('main-nav--closed')){navMain.classList.remove('main-nav--closed');navMain.classList.add('main-nav--opened');}else{navMain.classList.add('main-nav--closed');navMain.classList.remove('main-nav--opened');}});